import "./App.css";
import React, { useState } from "react";
import { BrowserRouter as Router, Route, Switch } from "react-router-dom";

function Home() {
  const handleClick = () => {
    window.location.href = "/first";
  };
  return (
    <div className="container">
      <div className="question">재혁아 나 사랑해?</div>
      <div className="choice-button">
        <button onClick={handleClick}>Yes</button>
      </div>
    </div>
  );
}

function First() {
  const [password, setPassword] = useState("");

  const onChange = (e) => {
    setPassword(e.target.value);
  };

  const handleLogin = () => {
    if (password == "120613") {
      window.location.href = "/second";
    } else {
      alert("우리 집 안올거야?");
    }
  };

  return (
    <div className="container">
      <div className="question">home password?</div>
      <div className="answer-input">
        <input type={"password"} onChange={onChange} value={password}></input>
      </div>
      <div className="choice-button">
        <button onClick={handleLogin}>로그인</button>
      </div>
    </div>
  );
}

function Second() {
  const [password, setPassword] = useState("");

  const onChange = (e) => {
    setPassword(e.target.value);
  };

  const handleLogin = () => {
    if (password == "120613") {
      window.location.href = "/third";
    } else {
      alert("우리 집 안올거야?");
    }
  };

  return (
    <div className="container">
      <div className="question">
        <p>
          그 날은 겨울이지만 춥지 않았어요. 재혁이를 만나 놀러가는 길이었는데,
        </p>
        <p>
          친구의 생일이어서 버스에서 문자를 보내다 멀미가 날 뻔도 했죠. 40분
          정도 버스를 타야해서 그런지 깜빡 졸았어요.
        </p>
        <p>
          눈을 다시 떠보니 재혁이가 햇빛을 손으로 막아주고 있더라구요. 예뻐
          죽겠어요 진짜.
        </p>
        <p>
          그 날 점심에 재혁이가 저한테 뭘 만들어줬는지 아세요? 진짜 너무 웃어서
          기절할 뻔 했잖아요 (웃음).
        </p>
        <p>
          그걸 만들고 난 뒤에 웃는 재혁이 모습이 얼마나 귀엽던지. 영상으로
          남겨놓기를 잘했어요
        </p>
      </div>
      <div className="answer-input">
        <input type={"password"} onChange={onChange} value={password}></input>
      </div>
      <div className="choice-button">
        <button onClick={handleLogin}>로그인</button>
      </div>
    </div>
  );
}

function Third() {
  const [password, setPassword] = useState("");

  const onChange = (e) => {
    setPassword(e.target.value);
  };

  const handleLogin = () => {
    if (password == "120613") {
      window.location.href = "/third";
    } else {
      alert("우리 집 안올거야?");
    }
  };
  <div className="container">
    <div className="question">
      <p></p>
    </div>
    <div className="answer-input">
      <input type={"password"} onChange={onChange} value={password}></input>
    </div>
    <div className="choice-button">
      <button onClick={handleLogin}>로그인</button>
    </div>
  </div>;
}

function App() {
  return (
    <Router>
      <div>
        <Switch>
          <Route exact path="/">
            <Home />
          </Route>
          <Route path="/first">
            <First />
          </Route>
          <Route path="/second">
            <Second />
          </Route>
          <Route path="/third">
            <Third />
          </Route>
        </Switch>
      </div>
    </Router>
  );
}

export default App;
